import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Course } from '../course';
import { CourseService } from '../course.service';
import { Module } from '../module';
import { ModuleService } from '../module.service';

@Component({
  selector: 'app-create-course',
  templateUrl: './create-course.component.html',
  styleUrls: ['./create-course.component.css']
})
export class CreateCourseComponent implements OnInit{

  course: Course = new Course();
  modules: Module[] = [];

  constructor(private moduleService: ModuleService,
    private courseService: CourseService,
    private router: Router){}

  ngOnInit(): void {
    this.loadModules();
  }

  loadModules() {
    this.moduleService.getModulesList().subscribe(
      (data: Module[]) => {
        console.log('Modules data:', data); // Log fetched modules data
        if (data && data.length > 0) {
          this.modules = data; // Update the modules array only if data is not empty
        } else {
          console.log('No modules data received.'); // Log if no data is received
        }
      },
      (error) => {
        console.error('Error fetching modules:', error); // Log any errors
      }
    );
  }

  saveCourse(){
    this.courseService.createCourse(this.course).subscribe( data =>{
      console.log(data);
      this.goToCourseList();
    },
    error => console.log(error));
  }

  goToCourseList(){
    this.router.navigate(['/courses'])
  }

  onSubmit(){
    console.log(this.course);
    this.saveCourse();
  }

}
