import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';
import { Course } from '../course';
import { CourseService } from '../course.service';
import { Module } from '../module';
import { ModuleService } from '../module.service';

@Component({
  selector: 'app-create-course',
  templateUrl: './create-course.component.html',
  styleUrls: ['./create-course.component.css']
})
export class CreateCourseComponent implements OnInit {

  protected aFormGroup!: FormGroup;
  course: Course = new Course();
  modules: Module[] = [];

  constructor(private moduleService: ModuleService,
              private courseService: CourseService,
              private router: Router,
              private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.aFormGroup = this.formBuilder.group({
      recaptcha: ['', Validators.required]
    });
    this.loadModules();
  }

  siteKey:

  loadModules() {
    this.moduleService.getModulesList().subscribe(
      (data: Module[]) => {
        this.modules = data;
        // Initialize course with the first module
        if (this.modules.length > 0) {
          this.course.module = this.modules[0];
        }
      },
      (error) => {
        console.log(error);
      }
    );
  }

  saveCourse() {
    this.courseService.createCourse(this.course).subscribe(data => {
        console.log(data);
        this.goToCourseList();
      },
      error => console.log(error));
  }

  goToCourseList() {
    this.router.navigate(['/courses']);
  }

  onSubmit() {
    console.log(this.course);
    this.saveCourse();
  }
}
<div class="col-md-6 offset-md-3">
    <h3>Create Course</h3>
    <form (ngSubmit)="onSubmit()" #moduleForm="ngForm">
        <div class="form-group">
            <label>Course Name</label>
            <input type="text" class="form-control" [(ngModel)]="course.courseName" name="courseName">
        </div>

        <div class="form-group">
            <label>Course Code</label>
            <input type="text" class="form-control" [(ngModel)]="course.courseCode" name="courseCode">
        </div>

        <div class="form-group">
            <label>Module</label>
            <select class="form-control" [(ngModel)]="course.module.moduleId" name="moduleId">
                <option *ngFor="let module of modules; let i = index" [value]="module.moduleId" [selected]="i === 0">{{ module.moduleName }}</option>
            </select>
        </div>

        <button class="btn btn-success" type="submit" [disabled]="!moduleForm.valid">Submit</button>
    </form>
    <form [formGroup]="aFormGroup">
        <ngx-recaptcha2 #captchaElem
          [siteKey]="siteKey"
          formControlName="recaptcha">
        </ngx-recaptcha2>
      </form>
</div>
