import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Course } from '../course';
import { CourseService } from '../course.service';

@Component({
  selector: 'app-course-list',
  templateUrl: './course-list.component.html',
  styleUrls: ['./course-list.component.css']
})
export class CourseListComponent implements OnInit{
  courses!: Course[];
  fileContent: string = '';

  constructor(private courseService: CourseService,
    private router: Router){}

  ngOnInit(): void {
    this.getCourses();
  }

  private getCourses(){
    this.courseService.getCoursesList().subscribe(data => {
      this.courses = data;
    })
  }

  updateCourse(courseId: number){
    this.router.navigate(['update-course',courseId]);
  }

  deleteCourse(courseId: number){
    this.courseService.deleteCourse(courseId).subscribe( data => {
      console.log(data);
      this.getCourses();
    })
  }

  getFileUrl(attachmentFileName: string): string {
    // Assuming your backend serves files from a specific route, construct the URL accordingly
    return `http://localhost:8081/course/files/${attachmentFileName}`;
  }

  getFileContent(fileName: string) {
    this.courseService.getFileContent(fileName).subscribe((data: string) => {
      // Store the content of the file
      this.fileContent = data;
      // Open the content in a new tab if newWindow is not null
      const newWindow = window.open();
      if (newWindow) {
        newWindow.document.write(this.fileContent);
      } else {
        console.error('Failed to open new window');
      }
    });
  }

  getFileType(attachmentFileName: string): string {
    const extension = attachmentFileName.split('.').pop()?.toLowerCase();
    if (extension === 'pdf') {
      return 'pdf';
    } else {
      return 'text';
    }
  }

}
<div class="mb-4">
  <a routerLink="/create-course" routerLinkActive="active" class="btn btn-primary">Add Course</a>
</div>
<h2>Courses List</h2>
<table class="table table-striped">
  <thead>
    <tr>
      <th>Course Name</th>
      <th>Course Code</th>
      <th>Module</th>
      <th>Attachment</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let course of courses">
      <td>{{ course.courseName }}</td>
      <td>{{ course.courseCode }}</td>
      <td>{{ course.module.moduleName }}</td>
      <td>
        <!-- Display attachment file name as a link -->
        <a [href]="getFileUrl(course.attachmentFileName)" target="_blank">{{ course.attachmentFileName }}</a>
      </td>
      <td>
        <button (click) = "previewFile(course.attachmentFileName)" class="btn btn-primary">Preview</button>
        <button (click) = "updateCourse(course.courseId)" class="btn btn-info">Update</button>
        <button (click) = "deleteCourse(course.courseId)" class="btn btn-danger" style="margin-left: 10px">Delete</button>
      </td>
    </tr>
  </tbody>
</table>
