import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-backoffice',
  templateUrl: './backoffice.component.html',
  styleUrls: ['/angular-frontend/src/app/backoffice/assets/vendor/bootstrap/css/bootstrap.min.css',
  '/angular-frontend/src/app/backoffice/assets/vendor/fonts/circular-std/style.css',
  '/angular-frontend/src/app/backoffice/assets/libs/css/style.css',
  '/angular-frontend/src/app/backoffice/assets/vendor/fonts/fontawesome/css/fontawesome-all.css',
  '/angular-frontend/src/app/backoffice/assets/vendor/charts/chartist-bundle/chartist.css',
  '/angular-frontend/src/app/backoffice/assets/vendor/charts/morris-bundle/morris.css',
  'assets/vendor/fonts/material-design-iconic-font/css/materialdesignicons.min.css',
  'assets/vendor/charts/c3charts/c3.css',
  'assets/vendor/fonts/flag-icon-css/flag-icon.min.css'
]
})
export class BackofficeComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
    this.loadScripts();
  }

  loadScripts() {
    // Array of script URLs to load
    const scriptUrls = [
      '',
      'assets/vendor/jquery/jquery-3.3.1.min.js',
      'assets/vendor/bootstrap/js/bootstrap.bundle.js',
      'assets/vendor/slimscroll/jquery.slimscroll.js',
      'assets/libs/js/main-js.js',
      'assets/vendor/charts/chartist-bundle/chartist.min.js',
      'assets/vendor/charts/sparkline/jquery.sparkline.js',
      'assets/vendor/charts/morris-bundle/raphael.min.js',
      'assets/vendor/charts/morris-bundle/morris.js',
      'assets/vendor/charts/c3charts/c3.min.js',
      'assets/vendor/charts/c3charts/d3-5.4.0.min.js',
      'assets/vendor/charts/c3charts/C3chartjs.js',
      'assets/libs/js/dashboard-ecommerce.js'
    ];

    // Load each script
    scriptUrls.forEach(url => {
      this.loadScript(url).then(() => {
        console.log(`${url} loaded`);
      }).catch(error => console.log('Error loading script:', error));
    });
  }

  loadScript(src: string): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      const script = document.createElement('script');
      script.src = src;
      script.onload = () => resolve();
      script.onerror = (error) => reject('Error loading script: ' + error);
      document.body.appendChild(script);
    });
  }

}
