import { Component } from '@angular/core';
import { FormGroup, Validators, FormBuilder } from '@angular/forms';
import { OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Module } from '../module';
import { ModuleService } from '../module.service';

@Component({
  selector: 'app-create-module',
  templateUrl: './create-module.component.html',
  styleUrls: ['./create-module.component.css']
})
export class CreateModuleComponent implements OnInit {

  protected aFormGroup!: FormGroup;
  module: Module = new Module();
  constructor(private moduleService: ModuleService,
    private router: Router,
    private formBuilder: FormBuilder) {}

  ngOnInit(): void{
  }

  saveModule(){
    this.moduleService.createModule(this.module).subscribe( data =>{
      console.log(data);
      this.goToModuleList();
    },
    error => console.log(error));
  }

  goToModuleList(){
    this.router.navigate(['/modules']);
  }

  onSubmit(){
    console.log(this.module);
    this.saveModule();
  }
}
<div class="col-md-6 offset-md-3">
    <h3> Create Module </h3>
    <form (ngSubmit)="onSubmit()" #moduleForm="ngForm">
        <div class="form-group">
            <label>Module Name</label>
            <input type="text" class="form-control" id="moduleName"
                [(ngModel)]="module.moduleName" name="moduleName" required>
        </div>

        <div class="form-group">
            <label>Description</label>
            <input type="text" class="form-control" id="description"
                [(ngModel)]="module.description" name="description" required>
        </div>

        <button class="btn btn-success" type="submit" [disabled]="!moduleForm.valid">Submit</button>
    </form>
</div>
