import { Component } from '@angular/core';
import { Course } from 'src/app/course';
import { CourseService } from 'src/app/course.service';
import { Module } from 'src/app/module';
import { ModuleService } from 'src/app/module.service';

@Component({
  selector: 'app-homefront',
  templateUrl: './homefront.component.html',
})
export class HomefrontComponent {
  modules: Module[] = [];
  course: Course = new Course();
  showPopup: boolean = false;
  selectedModule: Module | null = null;
  currentPage: number = 1;
  pageSize: number = 3; 
  showAddCoursePopup: boolean = false;
  selectedFile: File | undefined;

  constructor(private moduleService: ModuleService,
    private courseService: CourseService) {}

  ngOnInit(): void {
    this.moduleService.getModulesList().subscribe((data: Module[]) => {
      this.modules = data;
    });
  }

  openAddCoursePopup(): void {
    this.showAddCoursePopup = true;
  }

  closeAddCoursePopup(): void {
    this.showAddCoursePopup = false;
  }

  showCoursesPopup(event: MouseEvent, module: Module): void {
    event.preventDefault();
    this.selectedModule = module;
    this.showPopup = true;
  }

  closePopup(): void {
    this.showPopup = false;
  }

  getFileUrl(attachmentFileName: string): string {
    return `http://localhost:8081/course/files/${attachmentFileName}`;
  }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  saveCourse() {
    const formData = new FormData();
    formData.append('file', this.selectedFile as Blob); 
    formData.append('course', JSON.stringify(this.course));

    this.courseService.createCourse(formData).subscribe(data => {
        console.log(data);
      },
      error => console.log(error));
  }

  onSubmit() {
    console.log(this.course);
    this.saveCourse();
  }

  previewFile(fileUrl: string) {
    fetch(fileUrl)
      .then(response => response.blob())
      .then(blob => {
        const blobUrl = URL.createObjectURL(blob);
        window.open(blobUrl, '_blank');
      })
      .catch(error => {
        console.error('Error fetching file:', error);
      });
  }
}
