import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Course } from '../course';
import { CourseService } from '../course.service';
import { Module } from '../module';

@Component({
  selector: 'app-update-course',
  templateUrl: './update-course.component.html',
  styleUrls: ['./update-course.component.css']
})
export class UpdateCourseComponent implements OnInit{

  courseId!: number;
  course: Course = new Course();
  modules: Module[] = [];
  constructor(private courseService: CourseService,
    private route: ActivatedRoute,
    private router: Router){}

    ngOnInit(): void {
      this.courseId = this.route.snapshot.params["courseId"];

      // Fetch the course by its ID
      this.courseService.getCourseById(this.courseId).subscribe(data => {
        this.course = data;

        // Fetch the list of modules
        this.courseService.getModulesList().subscribe(modules => {
          this.modules = modules;

          // If the course has a module associated with it
          if (this.course.module) {
            // Find the selected module from the list of modules
            const selectedModule = this.modules.find(module => module.moduleId === this.course.module.moduleId);
            if (selectedModule) {
              // Update the course's module with the selected module
              this.course.module = selectedModule;
            }
          }
        }, error => console.log(error));
      }, error => console.log(error));
    }

    onSubmit() {
      console.log('Submitting update for course:', this.course);
      this.courseService.updateCourse(this.courseId, this.course).subscribe(
        data => {
          console.log('Update successful:', data);
          this.goToCourseList();
        }, 
        error => console.error('Update failed:', error)
      );
    }

  goToCourseList() {
    this.router.navigate(['/courses']);
  }

}
<div class="col-md-6 offset-md-3">
    <h3>Create Course</h3>
    <form (ngSubmit)="onSubmit()">
        <input type="hidden" [(ngModel)]="course.courseId" name="courseId">
        <div class="form-group">
            <label>Course Name</label>
            <input type="text" class="form-control" [(ngModel)]="course.courseName" name="courseName">
        </div>

        <div class="form-group">
            <label>Course Code</label>
            <input type="text" class="form-control" [(ngModel)]="course.courseCode" name="courseCode">
        </div>

        <div class="form-group">
            <label>Module</label>
            <select class="form-control" [(ngModel)]="course.module.moduleId" name="moduleId">
                <option *ngFor="let module of modules; let i = index" [value]="module.moduleId" [selected]="i === 0">{{ module.moduleName }}</option>
            </select>
        </div>

        <button class="btn btn-success" type="submit">Submit</button>
    </form>
</div>
