import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Course } from '../course';
import { CourseService } from '../course.service';
import { Module } from '../module';
import { ModuleService } from '../module.service';

@Component({
  selector: 'app-create-course',
  templateUrl: './create-course.component.html',
  styleUrls: ['./create-course.component.css']
})
export class CreateCourseComponent implements OnInit{

  course: Course = new Course();
  modules: Module[] = [];

  constructor(private moduleService: ModuleService,
    private courseService: CourseService,
    private router: Router){}

  ngOnInit(): void {
    this.loadModules();
  }

  loadModules() {
    this.moduleService.getModulesList().subscribe(
      (data: Module[]) => {
        console.log('Received modules data:', data); // Log received modules data
        this.modules = data; // Assign received data to the modules array
      },
      (error) => {
        console.error('Error fetching modules:', error); // Log any errors
      }
    );
  }

  saveCourse(){
    this.courseService.createCourse(this.course).subscribe( data =>{
      console.log(data);
      this.goToCourseList();
    },
    error => console.log(error));
  }

  goToCourseList(){
    this.router.navigate(['/courses'])
  }

  onSubmit(){
    console.log(this.course);
    this.saveCourse();
  }

}
<div class="col-md-6 offset-md-3">
    <h3>Create Course</h3>
    <form (ngSubmit)="onSubmit()">
        <div class="form-group">
            <label>Course Name</label>
            <input type="text" class="form-control" [(ngModel)]="course.courseName" name="courseName">
        </div>

        <div class="form-group">
            <label>Course Code</label>
            <input type="text" class="form-control" [(ngModel)]="course.courseCode" name="courseCode">
        </div>

        <div class="form-group">
            <label>Module</label>
            <select class="form-control" [(ngModel)]="course.module.moduleId" name="moduleId">
                <option *ngIf="modules.length > 0" [value]="modules[0].moduleId" selected>{{ modules[0].moduleName }}</option>
                <option *ngFor="let module of modules.slice(1)" [value]="module.moduleId">{{ module.moduleName }}</option>
            </select>
        </div>

        <button class="btn btn-success" type="submit">Submit</button>
    </form>
</div>
