import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Course } from '../course';
import { CourseService } from '../course.service';

@Component({
  selector: 'app-course-list',
  templateUrl: './course-list.component.html',
  styleUrls: ['./course-list.component.css']
})
export class CourseListComponent implements OnInit{
  courses!: Course[];

  constructor(private courseService: CourseService,
    private router: Router){}

  ngOnInit(): void {
    this.getCourses();
  }

  private getCourses(){
    this.courseService.getCoursesList().subscribe(data => {
      this.courses = data;
    })
  }

  updateCourse(courseId: number){
    this.router.navigate(['update-course',courseId]);
  }

}
<h2>Courses List</h2>
<table class="table table-striped">
  <thead>
    <tr>
      <th>Course Name</th>
      <th>Course Code</th>
      <th>Module</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let course of courses">
      <td>{{ course.courseName }}</td>
      <td>{{ course.courseCode }}</td>
      <td>{{ course.module.moduleName }}</td>
      <td>
        <button (click) = "updateCourse(course.courseId)" class="btn btn-info">Update</button>
        <button (click) = "deleteCoures(course.courseId)" class="btn btn-danger" style="margin-left: 10px">Delete</button>
      </td>
    </tr>
  </tbody>
</table>
